import React from "react";
import { useEffect, useState } from "react";
import axios from "axios";

export default function TimeTable(props) {
  const [form, setform] = useState(true);
  const [year, setyear] = useState(1);
  const [term, setterm] = useState(1);
  const [schedule, setschedule] = useState(null);

  const baseUrl = "http://localhost:8080/api/faculty/getschedule/";
  const callAPI = async (id, y, t) => {
    const url = baseUrl + id + "/values?year=" + y + "&term=" + t;
    const response = await axios.get(url);
    setschedule(response.data);
  };

  console.log(schedule);

  const handleSubmit = (y, t) => {
    setform(false);
    setyear(y);
    setterm(t);
    callAPI(props.id, year, term);
    console.log("Cliked");
  };
  const obj = JSON.stringify(schedule);
  return (
    <>
      {form == true && (
        <div>
          <select
            className="form-select"
            aria-label="Default select example"
            onChange={(e) => {
              setyear(e.target.value);
            }}
          >
            <option selected="">Year</option>
            <option value={1}>One</option>
            <option value={2}>Two</option>
          </select>
          <select
            className="form-select"
            aria-label="Default select example"
            onChange={(e) => {
              setterm(e.target.value);
            }}
          >
            <option selected="">Term</option>
            <option value={1}>One</option>
            <option value={2}>Two</option>
          </select>
          <button
            onClick={() => handleSubmit(year, term)}
            type="submit"
            className="btn btn-primary btn-lg"
          >
            Submit
          </button>
        </div>
      )}
      {form == false && (
        <div>
            <table className="table">
              <thead className="thead-dark">
                <tr>
                  <th scope="col">#</th>
                  <th scope="col">MON</th>
                  <th scope="col">TUE</th>
                  <th scope="col">WED</th>
                  <th scope="col">THU</th>
                  <th scope="col">FRI</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <th scope="row">9AM-11AM</th>
                  <td>{obj[0].MON}</td>
                  <td>Otto</td>
                  <td>@mdo</td>
                </tr>
                <tr>
                  <th scope="row">2</th>
                  <td>Jacob</td>
                  <td>Thornton</td>
                  <td>@fat</td>
                </tr>
                <tr>
                  <th scope="row">3</th>
                  <td>Larry</td>
                  <td>the Bird</td>
                  <td>@twitter</td>
                </tr>
              </tbody>
            </table>
            {/* <table className="table">
              <thead className="thead-light">
                <tr>
                  <th scope="col">#</th>
                  <th scope="col">First</th>
                  <th scope="col">Last</th>
                  <th scope="col">Handle</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <th scope="row">1</th>
                  <td>Mark</td>
                  <td>Otto</td>
                  <td>@mdo</td>
                </tr>
                <tr>
                  <th scope="row">2</th>
                  <td>Jacob</td>
                  <td>Thornton</td>
                  <td>@fat</td>
                </tr>
                <tr>
                  <th scope="row">3</th>
                  <td>Larry</td>
                  <td>the Bird</td>
                  <td>@twitter</td>
                </tr>
              </tbody>
