import logo from "./logo.svg";
import "./App.css";
import ServiceLogin from "./Service/ServiceLogin";
import NavBar from "./Components/NavBar";
import Login from "./Components/Login";
import Dashboard from "./Components/dashboard";
import Students from "./Components/students";
import TimeTable from "./Components/TimeTable";

import { useState } from "react";
import {BrowserRouter as Router, Routes, Route} from "react-router-dom";

function App() {
  const [user, setUser] = useState(null);
  const [notification, setNotification] = useState(null);
  const [notificationType, setNotificationType] = useState(null);
  const [loggedin, setloggedin] = useState(false);
  const [ gotos, setgotos] = useState(false);
  const [gotoTime,setgotoTime] = useState(false);
  const notificationHandler = (message, type) => {
    setNotification(message);
    setNotificationType(type);

    setTimeout(() => {
      setNotificationType(null);
      setNotification(null);
    }, 3000);
  };

  const StartLogin = async (credentials) => {
    try {
      console.log(credentials);
      const userObject = await ServiceLogin.login(credentials);
      setUser(userObject);
      window.localStorage.setItem("loggedInUser", JSON.stringify(userObject));

      notificationHandler(
        `Logged in successfully as ${userObject.firstName}`,
        "success"
      );
      //setBills([]);
      console.log("Login Success");
    } catch (exception) {
      console.log("Login Failed");
      notificationHandler(
        `Log in failed, check username and password entered`,
        "error"
      );
    }
  };

  const handleStudentAction = () => {
    setgotos(true);

    const handletimetable = () =>{
      setgotoTime(true);
    }
  }
  return (
    <>
      <NavBar title="IIIT Bangalore" />

      {user === null && <Login startLogin={StartLogin} />}

      {/* {
        user !== null && 
      <Dashboard user={user} setUser={setUser}/>
      } */}

      {gotoTime==false && gotos == false && user != null && (

        <div className="row text-center">
          <h1>{user.data}</h1>
          <div className="col col-sm-4">
            <button onClick={handletimetable} className="btn btn-primary px-5 mb-5 w-100">
              Time Table
            </button>
          </div>
          <div className="col col-sm-4">
            <button onClick={handleStudentAction} className="btn btn-primary px-5 mb-5 w-100">
              Students
            </button>
          </div>
        </div>
      )}

     {gotoTime == false && gotos ==true && user!=null &&<Students id = {user.data}/> }
     {gotoTime == true && gotos== false && user != null &&<TimeTable id={user.data}/>} 
    </>
  );
}

export default App;
