import React, { useEffect, useState } from "react";
import axios from 'axios';
import { Link } from 'react-router-dom';
import StudentList from "./studentList";


export default function Students(props) {
    const [ gotosList, setgotosList] = useState(false);

    let newid;
    const studentListAction = (id) => {
        console.log(id);
        newid=id;
        setgotosList(true);
      }

    const loginBaseUrl = "http://localhost:8080/api/course/get/";
    const [courses, setCourses] = useState([]);



    let id=props.id;
    useEffect(() => {
        loadCourses(id);
    },[]);


    const loadCourses = async (id) => {
        const response = await axios.get(loginBaseUrl+id);
        // console.log(response.data);
        setCourses(response.data);

        console.log("From function")
        // return response.data;
    };
    // let courses1 = loadCourses(id);
    // console.log(courses1);

    if (courses.length == 0) return null;
    console.log(courses);

  return (
    <>  
    {gotosList==null &&<div>
            <table className="table table-warning shadow table-hover table-striped">
          <thead>
            <tr>
              <th scope="col">Course ID</th>
              <th scope="col">Course Name</th>
              <th scope="col"></th>
            </tr>
          </thead>
          <tbody>
            {courses.map((domain, index) => (
              <tr key={domain.id}>
                <td>{domain.id}</td>
                <td>{domain.name}</td>
                <td><button onClick={()=>studentListAction(domain.id)} className="btn btn-primary">View Students</button> </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>}


     {gotosList ==true  && <StudentList id = {newid}/> } 
    </>
  );
}

